import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Spinner from './Spinner';

const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;

function Tag(){
    const [gif,setGif] = useState('');
    const [formData,setFormData] = useState({in:''});

    const [loading,setLoading]=useState('false');

    // function for api call
    async function fetchData(){
        setLoading(true);
        const url = `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
        const {data} = await axios.get(url); 
        const imageSource = data.data.images.downsized_large.url;
        setGif(imageSource);
        setLoading(false);
    }

    useEffect(()=>{
        fetchData();
    },[])

    function clickHandler(){
        fetchData();
    }


    
    return(
        <div className='w-1/2 bg-blue-500 rounded-lg border border-black flex flex-col items-center gap-y-5 mt-[15px]'>

          <h1 className='mt-[15px] text-2xl underline uppercase font-bold'>Random Gif</h1>

          {
            loading ? (<Spinner/>):(<img src={gif} alt='gif' width="450" />)
          }

          <input type='text' name='in' value={formData.in} onChange={changeHandler} 
          className=''/>

          <button className='w-10/12 bg-yellow-500 text-lg py-2 rounded-lg mb-[20px]'
          onClick={clickHandler}>
            Generate
        </button>
        </div>
    );
} 

export default Tag;
